import serialimport time# Open the serial port (Change 'COM3' to your actual port)ser = serial.Serial('/dev/tty.usbserial-AQ018ZLY', 9600, timeout=1)  # Windows Example# ser = serial.Serial('/dev/ttyUSB0', 9600, timeout=1)  # Linux Example# ser = serial.Serial('/dev/tty.usbserial', 9600, timeout=1)  # Mac Exampletime.sleep(2)  # Wait for connection to stabilize# Send a test message to PICmessage = "Hello PIC\n"ser.write(message.encode())  # Send message# Read the response from the PICresponse = ser.read(16)  # Read up to 16 bytesprint("Received from PIC:", response.decode())# Close the serial portser.close()